import React, { useState, useEffect, useRef } from 'react';
import { Row, Col, Card, Form, Button, ListGroup, Modal } from 'react-bootstrap';
import axios from 'axios';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isTyping, setIsTyping] = useState(false);
  const [showConversationModal, setShowConversationModal] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    loadConversations();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadConversations = async () => {
    try {
      const response = await axios.get('/api/chat/conversations');
      setConversations(response.data.conversations);
    } catch (error) {
      console.error('Error loading conversations:', error);
    }
  };

  const loadConversation = async (conversationId) => {
    try {
      const response = await axios.get(`/api/chat/conversations/${conversationId}/messages`);
      setMessages(response.data.messages);
      setCurrentConversationId(conversationId);
    } catch (error) {
      console.error('Error loading conversation:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    
    if (!inputMessage.trim() || isTyping) return;

    const userMessage = inputMessage.trim();
    setInputMessage('');
    setIsTyping(true);

    // Add user message to chat immediately
    const newUserMessage = {
      content: userMessage,
      sender_type: 'user',
      timestamp: new Date().toISOString()
    };
    setMessages(prev => [...prev, newUserMessage]);

    try {
      const response = await axios.post('/api/chat/send', {
        message: userMessage,
        conversation_id: currentConversationId
      });

      // Update conversation ID if this is a new chat
      if (!currentConversationId) {
        setCurrentConversationId(response.data.conversation_id);
        loadConversations(); // Refresh conversation list
      }

      // Add bot response
      const botMessage = {
        content: response.data.response,
        sender_type: 'bot',
        timestamp: response.data.timestamp
      };
      setMessages(prev => [...prev, botMessage]);

    } catch (error) {
      console.error('Error sending message:', error);
      // Add error message with more details
      const errorDetails = error.response?.data?.error || error.message || 'Unknown error';
      const errorMessage = {
        content: `Sorry, I encountered an error: ${errorDetails}. Please try again.`,
        sender_type: 'bot',
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsTyping(false);
    }
  };

  const startNewChat = () => {
    setCurrentConversationId(null);
    setMessages([]);
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now - date;

    if (diff < 60000) return 'Just now';
    if (diff < 3600000) return `${Math.floor(diff / 60000)}m ago`;
    if (diff < 86400000) return `${Math.floor(diff / 3600000)}h ago`;
    return date.toLocaleDateString();
  };

  const renderMessage = (message, index) => {
    const isUser = message.sender_type === 'user';
    return (
      <div key={index} className={`message ${isUser ? 'user' : 'bot'}`}>
        {!isUser && (
          <div className="message-avatar">
            <i className="fas fa-robot"></i>
          </div>
        )}
        <div className="message-bubble">
          {message.content}
          <small className="message-time">
            {formatTimestamp(message.timestamp)}
          </small>
        </div>
        {isUser && (
          <div className="message-avatar">
            <i className="fas fa-user"></i>
          </div>
        )}
      </div>
    );
  };

  return (
    <Row className="h-100">
      {/* Sidebar - Conversation List */}
      <Col md={3} className="d-none d-md-block">
        <Card className="h-100">
          <Card.Header className="bg-secondary text-white d-flex justify-content-between align-items-center">
            <h6 className="mb-0">Conversations</h6>
            <Button size="sm" variant="light" onClick={startNewChat}>
              <i className="fas fa-plus"></i>
            </Button>
          </Card.Header>
          <Card.Body className="p-0">
            <ListGroup variant="flush">
              {conversations.length === 0 ? (
                <ListGroup.Item className="text-center text-muted py-4">
                  <i className="fas fa-comment-slash fa-2x mb-2 d-block"></i>
                  No conversations yet
                </ListGroup.Item>
              ) : (
                conversations.map(conv => (
                  <ListGroup.Item
                    key={conv.id}
                    action
                    active={conv.id === currentConversationId}
                    onClick={() => loadConversation(conv.id)}
                    className="conversation-item"
                  >
                    <div className="conversation-title">{conv.title}</div>
                    <div className="conversation-preview">
                      {conv.message_count} messages
                    </div>
                    <small className="text-muted">
                      {formatTimestamp(conv.updated_at)}
                    </small>
                  </ListGroup.Item>
                ))
              )}
            </ListGroup>
          </Card.Body>
        </Card>
      </Col>

      {/* Main Chat Area */}
      <Col md={9}>
        <Card className="chat-container">
          {/* Chat Header */}
          <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center">
            <h5 className="mb-0">
              <i className="fas fa-robot me-2"></i>
              AI Assistant
            </h5>
            <div>
              <Button
                size="sm"
                variant="light"
                onClick={startNewChat}
                className="me-2"
                title="Clear Chat"
              >
                <i className="fas fa-trash"></i>
              </Button>
              <Button
                size="sm"
                variant="light"
                className="d-md-none"
                onClick={() => setShowConversationModal(true)}
                title="Show Conversations"
              >
                <i className="fas fa-bars"></i>
              </Button>
            </div>
          </Card.Header>

          {/* Chat Messages */}
          <Card.Body className="chat-messages">
            {messages.length === 0 ? (
              <div className="welcome-message">
                <i className="fas fa-robot fa-3x mb-3"></i>
                <h5>Welcome to AI Chatbot!</h5>
                <p>Ask me anything and I'll do my best to help you.</p>
              </div>
            ) : (
              messages.map((message, index) => renderMessage(message, index))
            )}
            {isTyping && (
              <div className="message bot">
                <div className="message-avatar">
                  <i className="fas fa-robot"></i>
                </div>
                <div className="message-bubble">
                  <div className="loading-spinner me-2"></div>
                  AI is typing...
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </Card.Body>

          {/* Chat Input */}
          <Card.Footer>
            <Form onSubmit={sendMessage}>
              <div className="input-group">
                <Form.Control
                  type="text"
                  placeholder="Type your message here..."
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  disabled={isTyping}
                />
                <Button type="submit" variant="primary" disabled={isTyping}>
                  <i className="fas fa-paper-plane"></i>
                </Button>
              </div>
            </Form>
          </Card.Footer>
        </Card>
      </Col>

      {/* Mobile Conversation Modal */}
      <Modal show={showConversationModal} onHide={() => setShowConversationModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Conversations</Modal.Title>
        </Modal.Header>
        <Modal.Body className="p-0">
          <ListGroup variant="flush">
            {conversations.map(conv => (
              <ListGroup.Item
                key={conv.id}
                action
                active={conv.id === currentConversationId}
                onClick={() => {
                  loadConversation(conv.id);
                  setShowConversationModal(false);
                }}
              >
                <div className="conversation-title">{conv.title}</div>
                <div className="conversation-preview">
                  {conv.message_count} messages
                </div>
                <small className="text-muted">
                  {formatTimestamp(conv.updated_at)}
                </small>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Modal.Body>
      </Modal>
    </Row>
  );
};

export default Chat;
